/*
所有调用接口的代码都写到这里 
*/
import Vue from 'vue'
import axios from 'axios'
import VueAxios from 'vue-axios'
 
Vue.use(VueAxios, axios)

// 设置接口的基地址
Vue.axios.defaults.baseURL = 'https://www.easy-mock.com/mock/5b4dbf917e597b7eabfb579d/example'


// 获取所有的分类
function getCategories() {
    return Vue.axios.get('/categories')
}
// 获取所有的排名
function getTopn(category_id='', top=50){
    return Vue.axios.get('/top10s', {
        category_id,
        top
    })
}
// 登录
function login(username,password){
    return Vue.axios.post('/authorizations',{
        username,
        password
    })
}
//答题
function getTiByCatId(catId) {
    return Vue.axios.get('/categories'+catId+'/questions?page='+page+'&per_page='+per_page)
}

// 导出
export default {
    getCategories,
    getTopn,
    login,
    getTiByCatId
}
